import { takeEvery, call, put } from "redux-saga/effects";

import {
  firestore,
  convertCollectionsSnapshotToMap,
} from "../../firebase/firebase.utils";

import {
  fetchCollectionsSuccess,
  fetchCollectionsFailure,
} from "./shop.actions";

import ShopActionTypes from "./shop.types";

export function* fetchCollectionsAsync() {
  yield console.log("i am fire");

  try {
    const collectionRef = firestore.collection("collections");
    const snapshot = yield collectionRef.get();
    const collectionsMAp = yield call(
      convertCollectionsSnapshotToMap,
      snapshot
    );
  } catch (error) {}
  //becuase we are yielding this call it allows to defer control at this point of the execution to the saga middleware
  //so incase it hase to cancel we give it another place where it is able to do so
  //adding more yields like this also makes it eaiser to test
  //the call effect from redux saga, creates a description of the effect like in redux, where ypu use action creators to create a plain object describing the action that will get executed.

  // collectionRef
  //   .get()
  //   .then((snapshot) => {
  //     const collectionsMap = convertCollectionsSnapshotToMap(snapshot);
  //     dispatch(fetchCollectionsSuccess(collectionsMap));
  //   })
  //   .catch((error) => dispatch(fetchCollectionsFailure(error.message)));
}

export function* fetchCollectionsStart() {
  yield takeEvery(
    //creates a non blocking call
    //which makes it so that our application will not stop running and run other sagas or other things that we dont want to stop
    //it also alows us to stop things form happening
    //ex if the user has called for the data once but does so again we dont want that call to also run, so we stop the new call from running
    //this happens by us "yielding" over control of this saga to the saga middleware
    //which can determine whether to cancel or not any of the previously started sages from the other cactions that came in,
    //we cand control that because of the yield
    ShopActionTypes.FETCH_COLLECTIONS_START,
    //it first listend for this action
    fetchCollectionsAsync
    //then did this, and then to fetchcollectionAsync to console.log
  );
}
