import React from "react";
import StripeCheckout from "react-stripe-checkout";
import axios from "axios";

const StripeCheckoutButton = ({ price }) => {
  const priceForStripe = price * 100; //dollars to cents
  const publishablekey =
    "pk_test_51HizlGJLUgCXSYtp9PUApnP8CwRLjiJJ2ro2kKrMmdxmKACiETeIX622NlfQ1GuHAlpwpM8BzjcP3eVq5cOa8wOs00OmkPVqIX";

  const onToken = (token) => {
    axios({
      url: "payment",
      method: "post",
      data: {
        amount: priceForStripe,
        token,
      },
    })
      .then((response) => {
        alert("payment successfull");
      })
      .catch((error) => {
        console.log("payment error: ", JSON.parse(error));
        alert(
          "there was an issue with the payment, Please make sure to use the provided credit card"
        );
      });
    //do something here with popup
  };

  return (
    <StripeCheckout
      label="Pay Now"
      name="CRWN Clothing Ltd."
      billingAddress
      shippingAddress
      image="https://sendeyo.com/up/d/f3eb2117da"
      description={`Your Total is $${price}`}
      amout={priceForStripe}
      panelLabel="Pay Now"
      token={onToken}
      stripeKey={publishablekey}
    />
  );
};

export default StripeCheckoutButton;
