import UserAcctionTypes from "./user.types";

const INITIAL_STATE = {
  currentUser: null,
};

const userReducer = (state = INITIAL_STATE, action) => {
  //if state is undefined it will fallback to the value of initial state
  switch (action.type) {
    case UserAcctionTypes.GOOGLE_SIGN_IN_SUCCESS:
    case UserAcctionTypes.EMAIL_SING_IN_SUCCESS: //you can stack cases like this
      return {
        ...state, //everything on the state
        currentUser: action.payload, //set the current user
        error: null,
      };
    case UserAcctionTypes.GOOGLE_SIGN_IN_FAILURE:
    case UserAcctionTypes.EMAIL_SING_IN_FAILURE:
      return {
        ...state,
        error: action.payload,
      };
    default:
      return state; //returns the old object / state
  }
};

export default userReducer;
